import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'jxui-gifts-group-card[id][name][description][tags]',
  templateUrl: './gifts-group-card.component.html',
  styleUrls: ['./gifts-group-card.component.scss']
})
export class GiftsGroupCardComponent {
  @Input() id!: number;
  @Input() name!: string;
  @Input() description!: string;
  @Input() tags!: string[];
  @Output() selected = new EventEmitter<boolean>();

  public select(): void {
    this.selected.emit(true);
  }
}

import { Component, Input } from '@angular/core';
import { ResponsiveComponent } from '../responsive/responsive.component';

@Component({
  selector: 'jxui-gifts-group-list[groups]',
  templateUrl: './gifts-group-list.component.html',
  styleUrls: ['./gifts-group-list.component.scss']
})
export class GiftsGroupListComponent extends ResponsiveComponent {
  @Input() groups!: {
    id: number;
    name: string;
    description: string;
    tags: string[];
  }[];

  public select(group: this['groups'][number]): void {
    alert('Selected group: ' + group.name);
  }
}

